import React, { useContext, useEffect, useState } from 'react'
import Chat from '../Components/Complain/Chat'
import Contact from '../Components/Complain/Contact'
import NavBar from '../Components/NavBar'

import {io} from 'socket.io-client'
import { UserContext } from '../Context/User-Context'

let socket

export default function UserComplain() {

    const title = 'Complain'
    document.title = 'DumbMerch | ' + title

    const [contact, setContact] = useState(null)
    const [contacts, setContacts] = useState([])
    const [messages, setMessages] = useState([])
    const [state] = useContext(UserContext)

    useEffect(() => {
        socket = io('http://localhost:5000', {
            auth: {
                token: localStorage.getItem('token')
            },
            query: {
                id: state.user.id
            }
        })

        socket.on('new message', () => {
            console.log('contact', contact)
            console.log('triggered', contact?.id)
            socket.emit('load messages', contact?.id)
        })

        // Listen error sent from server
        socket.on('connect_error', (err) => {
            console.log(err.message)
        })
        loadContact()
        loadMessages()

        return () => socket.disconnect()

    }, [messages])

    const loadContact = () => {
        // Emit event load admin contact
        socket.emit('load admin contact')

        // Listen event to get admin contact
        socket.on('admin contact', async (data) => {

            // Manipulate data to add message property with the newest
            const dataContact = {
                ...data,
                message: messages.length > 0 ? messages[messages.length - 1].message : 'Click here to start Message'
            }
            setContacts([dataContact])
        })
    }

    // Used for active style when click contact
    const onClickContact = (data) => {
        setContact(data)
        // Emit event load messages
        socket.emit('load messages', data.id)
    }

    const loadMessages = (value) => {
        // Define listener on event message
        socket.on('messages', async (data) => {
            // Get data message
            if (data.length > 0) {
                const dataMessages = data.map((item) => ({
                    idSender: item.sender.id,
                    message: item.message
                }))
                console.log(dataMessages)
                setMessages(dataMessages)
            }
            const chatMessageElm = document.getElementById('chat-messages')
            chatMessageElm.scrollTop = chatMessageElm?.scrollHeight
        })
    }

    const onSendMessage = (e) => {
        // Listener only eneter key event press
        if (e.key === 'Enter') {
            const data = {
                idRecipient: contact.id,
                message: e.target.value
            }

            // Emit event send message
            socket.emit('send message', )
        }
    }

    return (
        <div className='bg'>
            <NavBar title={title} />
            <div className='mx-3' style={{height: '84vh'}}>
                <div className='d-flex'>
                    <div style={{height: '84vh'}} className="px-3 border-end border-dark overflow-auto">
                        <Contact dataContact={dataContact} setContact={setContact} contact={contact} />
                    </div>
                    <div style={{maxHeight: '84vh', flex: 1}} >
                        <Chat contact={contact} />
                    </div>
                </div>
            </div>
        </div>
    )
}
