import React, { useEffect, useState } from 'react'
import ProductCard from '../Components/Card/Product-Card'
import NavBar from '../Components/NavBar'
import SearchIcon from '@material-ui/icons/Search'
import CloseIcon from '@material-ui/icons/Close'
import { dataProduct } from '../Dummy/Product'

export default function Homepage() {

    const title = 'Product'
    document.title = 'DumbMerch | ' + title

    const [product, setProduct] = useState(dataProduct)
    const [searchItem, setSearchItem] = useState('')

    const handleChange = (e) => {
        
        setSearchItem('')
    }
    
  return (
    <div className='bg'>
        <NavBar title={title}  />
        <div className='mx-5 px-5 mt-5 pb-5'>
            <div className='search_bar'>
                <h3 style={{color: '#F74D4D', fontWeight: '700'}}>Product</h3>
                <div className='header_search'>
                    <input 
                        className='header_searchInput' 
                        type='text' 
                        style={{backgroundColor: '#353535', border: '1px solid #BCBCBC', color: 'white'}} 
                        onChange={(event) => setSearchItem(event.target.value) || ''}  
                    />
                    {searchItem.length === 0 ? (
                        <SearchIcon 
                        className='header_searchIcon px-1' 
                        style={{fontSize: '35px'}}
                        />
                    ) : (
                        <CloseIcon
                        className='header_searchIcon px-1' 
                        style={{fontSize: '35px'}}
                        onClick={() => setSearchItem('')}
                        />
                    )}
                </div>
            </div>
            <div>
                <div className='d-flex flex-wrap justify-content-around'>
                    {product.filter((item) => {
                        if (searchItem === '') {
                            return <ProductCard item={item} />
                        } else if (item.name.toLowerCase().includes(searchItem.toLowerCase())) {
                            return <ProductCard item={item} />
                        }
                    }).map((item, index) => {
                        return (
                            <ProductCard key={index} item={item} />
                        )
                    })}
                </div>
            </div>
        </div>
    </div>
  )
}
