import React, { useEffect, useState } from 'react'
import ProductCard from '../Components/Card/Product-Card'
import NavBar from '../Components/NavBar'
import SearchIcon from '@material-ui/icons/Search'
import CloseIcon from '@material-ui/icons/Close'
import { dataProduct } from '../Dummy/Product'

export default function Homepage() {

    const title = 'Product'
    document.title = 'DumbMerch | ' + title

    const [product, setProduct] = useState(dataProduct)
    const [searchItem, setSearchItem] = useState('')
  const [filterProduct, setFilterProduct] = useState([])

  // // Filter product 
  const handleFilter = (e) => {
    const word = e.target.value
    setSearchItem(word)

    const newFilter = product.filter((value) => {
      return value.name.toLowerCase().includes(word.toLowerCase())
    })

    if (word === '') {
      setFilterProduct([])
    } else {
      setFilterProduct(newFilter)
    }
  }
  // console.log(filterProduct)

  const clearInput = () => {
    setFilterProduct([])
    setSearchItem('')
  }
    
  return (
    <div className='bg'>
        <NavBar title={title}  />
        <div className='mx-5 px-5 mt-5 pb-5'>
            <div className='d-flex'>
                <h3 style={{color: '#F74D4D', fontWeight: '700'}}>Product</h3>
                <div className='header_search'>
                    <input 
                        className='header_searchInput' 
                        type='text' 
                        style={{backgroundColor: '#353535', border: '1px solid #BCBCBC', color: 'white'}} 
                        onChange={handleFilter} 
                        value={searchItem}  
                    />
                    {searchItem.length === 0 ? (
                        <SearchIcon 
                        className='header_searchIcon px-1' 
                        style={{fontSize: '35px'}}
                        />
                    ) : (
                        <CloseIcon
                        className='header_searchIcon px-1' 
                        style={{fontSize: '35px'}}
                        onClick={clearInput}
                        />
                    )}
                </div>
            </div>
            <div className='product_main'>
                {product ? (
                    <div className='d-flex flex-wrap justify-content-around'>
                        {product?.map((item, index) => (
                            <ProductCard key={index} item={item} />
                        ))}
                    </div>
                ) : (
                    <div className='d-flex flex-wrap justify-content-around'>
                    {filterProduct?.map((item, index) => (
                        <ProductCard key={index} item={item} />
                    ))}
                    </div>
                )}
                
            </div>
        </div>
    </div>
  )
}
